(function($){
    $.fn.tujie = function(options){
        var defaults = {
            tablist:".TujieList",
            playimg:".TujieLeft",
            beigin:0
        };

        var opts = $.extend(defaults, options);

        //定义通用参数
        var tabLi = $(opts.tablist).find("li");
        var tabImg = $(opts.playimg).find("div");
        var ZindexSize = [6,5,4,3,2,1];
        //tabLi.eq(0).addClass('t1');

        //左侧图片列表样式
        var tabimgshow = function(id){

        	tabImg.find(".mask").remove();
        	tabImg.eq(id).transition({zIndex:7,scale:1,right:0},200,'linear');

        	tabImg.each(function(index, el) {
        	       	   
          	   if (index != id) {
          	   	 $(this).append('<span class="mask"></span>');
          	   	 $(this).transition({right:20*index,zIndex:ZindexSize[index],scale:1-0.1*index});
          	   }

            });
        }
        tabimgshow(opts.beigin);
        //点击切换
        tabLi.bind("click",function(){
          var liIndex = $(this).index();
          //点击触发效果
          tabLi.each(function(index, el) {
          	$(this).find("b").transition({translate:[0,0]},150);
          	$(this).find(".line").transition({width:0,translate:[0,0]},150);
          	$(this).find('.line').remove();
            $(this).css({fontSize:"18px"});
            $(this).css({background:"#ce161b"});
          });
          $(this).css({fontSize:"20px"})
          $(this).append('<span class="line"></span>')
          $(this).find("b").transition({translate:[-80,0]},150);
          $(this).css({background:"#e41719"});
          setTimeout(function(){
            tabLi.eq(liIndex).find(".line").transition({width:90,translate:[-50,0]},150);
          },200)
          tabimgshow(liIndex);
        })
        //判断是否支持CSS3动画效果
             if(Modernizr.backgroundsize == false){
             }else{
               
             }
    };
})(jQuery);